// Setup module
// ------------------------------

var EchartsspeciesSalesOverviewByFarm = (function () {
  //
  // Setup module components
  //

  // Basic bar chart
  var _speciesSalesOverviewByFarmExample = function () {
    if (typeof echarts == "undefined") {
      console.warn("Warning - echarts.min.js is not loaded.");
      return;
    }

    // Define element
    var speciesSales_byFarm_overview_element = document.getElementById(
      "speciesSales_byFarm_overview"
    );

    //
    // Charts configuration
    //

    if (speciesSales_byFarm_overview_element) {
      // Initialize chart
      var speciesSales_byFarm_overview = echarts.init(speciesSales_byFarm_overview_element);

      //
      // Chart config
      //

      // Options
      speciesSales_byFarm_overview.setOption({
        // Global text styles
        textStyle: {
          fontFamily: "Roboto, Arial, Verdana, sans-serif",
          fontSize: 13,
        },

        // Chart animation duration
        animationDuration: 750,

        // Setup grid
        grid: {
          left: 0,
          right: 30,
          bottom: 0,
          top: 60,
          containLabel: true,
        },
        title: {
          text: "Species GP(RM)",
          textStyle: {
            fontSize: 14,
          },
          subtext: "Red Snapper",
        },

        // Add tooltip
        tooltip: {
          trigger: "axis",
          backgroundColor: "rgba(0,0,0,0.75)",
          padding: [10, 15],
          textStyle: {
            fontSize: 13,
            fontFamily: "Roboto, sans-serif",
          },
          axisPointer: {
            type: "shadow",
            shadowStyle: {
              color: "rgba(0,0,0,0.075)",
            },
          },
        },

        // Vertical axis
        yAxis: [
          {
            type: "value",
            boundaryGap: [0, 0.01],
            axisLabel: {
              color: "#333",
            },
            axisLine: {
              lineStyle: {
                color: "#999",
              },
            },
            splitLine: {
              show: true,
              lineStyle: {
                color: "#eee",
                type: "dashed",
              },
            },
          },
        ],

        // Horizontal
        xAxis: [
          {
            type: "category",
            data: ["2014", "2015", "2016", "2017", "2018", "2019", "2020", "2021"],
            axisLabel: {
              color: "#333",
            },
            axisLine: {
              lineStyle: {
                color: "#999",
              },
            },
            splitLine: {
              show: true,
              lineStyle: {
                color: ["#eee"],
              },
            },
            splitArea: {
              show: true,
              areaStyle: {
                color: ["rgba(250,250,250,0.1)", "rgba(0,0,0,0.015)"],
              },
            },
          },
        ],

        // Add series
        series: [
          {
            name: "Farm",
            type: "bar",
            symbolSize: 7,
            data: [120, 132, 101, 134, 590, 230, 210, 120],
            itemStyle: {
              normal: {
                borderWidth: 2,
                color: "#003f5c",
              },
            },
          },
          {
            name: "Farm",
            type: "bar",
            symbolSize: 7,
            data: [412, 231, 512, 235, 251, 125, 634, 356],
            itemStyle: {
              normal: {
                borderWidth: 2,
                color: "#58508d",
              },
            },
          },
          {
            name: "Farm",
            type: "bar",
            symbolSize: 7,
            data: [323, 512, 623, 823, 263, 892, 123, 834],
            itemStyle: {
              normal: {
                borderWidth: 2,
                color: "#bc5090",
              },
            },
          },
          {
            name: "Farm",
            type: "bar",
            symbolSize: 7,
            data: [626, 161, 612, 623, 412, 252, 23, 612],
            itemStyle: {
              normal: {
                borderWidth: 2,
                color: "#ff6361",
              },
            },
          },
          {
            name: "Farm",
            type: "bar",
            symbolSize: 7,
            data: [723, 234, 612, 612, 213, 346, 884, 233],
            itemStyle: {
              normal: {
                borderWidth: 2,
                color: "#ffa600",
              },
            },
          },
          {
            name: "Farm",
            type: "bar",
            symbolSize: 7,
            data: [323, 512, 623, 823, 633, 892, 123, 834],
            itemStyle: {
              normal: {
                borderWidth: 2,
                color: "#ff6e54",
              },
            },
          },
        ],
      });
    }

    //
    // Resize charts
    //

    // Resize function
    var triggerChartResize = function () {
      speciesSales_byFarm_overview_element && speciesSales_byFarm_overview.resize();
    };

    // On sidebar width change
    var sidebarToggle = document.querySelector(".sidebar-control");
    sidebarToggle && sidebarToggle.addEventListener("click", triggerChartResize);

    // On window resize
    var resizeCharts;
    window.addEventListener("resize", function () {
      clearTimeout(resizeCharts);
      resizeCharts = setTimeout(function () {
        triggerChartResize();
      }, 200);
    });
  };

  //
  // Return objects assigned to module
  //

  return {
    init: function () {
      _speciesSalesOverviewByFarmExample();
    },
  };
})();

// Initialize module
// ------------------------------

document.addEventListener("DOMContentLoaded", function () {
  EchartsspeciesSalesOverviewByFarm.init();
});
