/* ------------------------------------------------------------------------------
 *
 *  # Echarts - Basic bars example
 *
 *  Demo JS code for basic bar chart [light theme]
 *
 * ---------------------------------------------------------------------------- */

// Setup module
// ------------------------------

var EchartsspeciesSalesYearly = (function () {
  //
  // Setup module components
  //

  // Basic bar chart
  var _speciesSalesYearlyExample = function () {
    if (typeof echarts == "undefined") {
      console.warn("Warning - echarts.min.js is not loaded.");
      return;
    }

    // Define element
    var speciesSales_yearly_element = document.getElementById("speciesSales_yearly");

    //
    // Charts configuration
    //

    if (speciesSales_yearly_element) {
      // Initialize chart
      var speciesSales_yearly = echarts.init(speciesSales_yearly_element);

      //
      // Chart config
      //

      // Options
      speciesSales_yearly.setOption({
        // Global text styles
        textStyle: {
          fontFamily: "Roboto, Arial, Verdana, sans-serif",
          fontSize: 13,
        },

        // Chart animation duration
        animationDuration: 750,

        // Setup grid
        grid: {
          left: 0,
          right: 30,
          bottom: 0,
          top: 60,
          containLabel: true,
        },
        title: {
          text: "Species Sales(RM)",
          textStyle: {
            fontSize: 14,
          },
          subtext: "All Species",
        },

        // Add tooltip
        tooltip: {
          trigger: "axis",
          backgroundColor: "rgba(0,0,0,0.75)",
          padding: [10, 15],
          textStyle: {
            fontSize: 13,
            fontFamily: "Roboto, sans-serif",
          },
        },

        // Horizontal axis
        yAxis: [
          {
            type: "value",
            boundaryGap: [0, 0.01],
            axisLabel: {
              color: "#333",
            },
            axisLine: {
              lineStyle: {
                color: "#999",
              },
            },
            splitLine: {
              show: true,
              lineStyle: {
                color: "#eee",
                type: "dashed",
              },
            },
          },
        ],

        // Vertical axis
        xAxis: [
          {
            type: "category",
            data: [
              "2010",
              "2011",
              "2012",
              "2013",
              "2014",
              "2015",
              "2016",
              "2017",
              "2018",
              "2019",
              "2020",
              "2021",
            ],
            axisLabel: {
              color: "#333",
            },
            axisLine: {
              lineStyle: {
                color: "#999",
              },
            },
            splitLine: {
              show: true,
              lineStyle: {
                color: ["#eee"],
              },
            },
            splitArea: {
              show: true,
              areaStyle: {
                color: ["rgba(250,250,250,0.1)", "rgba(0,0,0,0.015)"],
              },
            },
          },
        ],

        // Add series
        series: [
          {
            name: "Farm",
            type: "line",
            symbolSize: 7,
            data: [120, 132, 101, 134, 90, 230, 210, 120, 132, 101, 134, 90],
            itemStyle: {
              normal: {
                borderWidth: 2,
                color: "#003f5c",
              },
            },
            label: {
              position: "top",
              offset: [0, -10],
              show: true,
            },
          },
        ],
      });
    }

    //
    // Resize charts
    //

    // Resize function
    var triggerChartResize = function () {
      speciesSales_yearly_element && speciesSales_yearly.resize();
    };

    // On sidebar width change
    var sidebarToggle = document.querySelector(".sidebar-control");
    sidebarToggle && sidebarToggle.addEventListener("click", triggerChartResize);

    // On window resize
    var resizeCharts;
    window.addEventListener("resize", function () {
      clearTimeout(resizeCharts);
      resizeCharts = setTimeout(function () {
        triggerChartResize();
      }, 200);
    });
  };

  //
  // Return objects assigned to module
  //

  return {
    init: function () {
      _speciesSalesYearlyExample();
    },
  };
})();

// Initialize module
// ------------------------------

document.addEventListener("DOMContentLoaded", function () {
  EchartsspeciesSalesYearly.init();
});
